class_args <- new_class(
  "class_args",
  properties = list(
    id     = new_union(NULL, class_numeric),
    state  = new_union(NULL, class_vector),
    name   = new_union(NULL, class_vector),
    number = new_union(NULL, class_numeric)
  ),
  validator = function(self) { 
    if (not_null(self@state) && 
        any(!self@state %in% state.abb)) 
      paste("`state`", self@state, "not found")
  }
