3.4 Querying

format_query_open &lt;- function(args, op = "=") {

  args  &lt;- discard(args, is.null)

  query &lt;- glue(
  "
  'conditions[{i}][property]' = '{PROPERTY}',
  'conditions[{i}][operator]' = '{OPERATOR}',
  'conditions[{i}][value]' = '{VALUE}'
  ",
    i                 = seq_along0(args),
    PROPERTY          = names(args),
    OPERATOR          = op,
    VALUE             = args) |&gt;
    glue_collapse(sep = ",\n")

  glue("c({query})") |&gt;
    parse_expr() |&gt;
    eval_bare()
}

create_request_open &lt;- function(id, args) {

  id |&gt;
    open_uuid_url() |&gt;
    request() |&gt;
    req_url_query(
      !!!format_query_open(args),
      schema  = "false",
      keys    = "false",
      offset  = 0
    )
}

query_nresults_open &lt;- function(req) {

  req_url_query(
    req,
    results = "false", 
    count   = "true", 
    offset  = 0, 
    limit   = 1) |&gt;
    perform_simple() |&gt; 
    _[["count"]]

}

perform_request_open &lt;- function(req) {

  req_url_query(
    req,
    results = "true", 
    count   = "false", 
    offset  = 0, 
    limit   = 500) |&gt;
    perform_simple()

}

