9.0.1 class_Identifier
class_Identifier &lt;- new_class(
  name       = "class_Identifier",
  properties = list(
    url      = new_property(
      class  = NULL | class_list,
      setter = function(self, value) {
        if (not_null(value)) {
          self@url &lt;- url_parse(value)
          self
        }}),
    totalRows = new_property(
      class  = NULL | class_integer,
      getter = function(self) {
        if (not_null(self@url)) {
          url_build(self@url) |&gt;
            request() |&gt;
            req_url_path_append("stats") |&gt;
            req_perform() |&gt;
            resp_body_json(simplifyVector = TRUE) |&gt;
            gelm("total_rows")
        }})
    )
)

10.2 Main Data

main &lt;- as_tbl(
    fload(
      "https://data.cms.gov/data.json", 
      query = "/dataset") |&gt;
      slt(
        title,
        keyword,
        description,
        periodicity = accrualPeriodicity,
        modified,
        temporal,
        distribution,
        identifier,
        contactPoint,
        dictionary = describedBy,
        landing = landingPage,
        references
      ) |&gt;
      mtt(
        modified = as_date(modified),
        periodicity = recode_iso8601(periodicity),
        keyword = flatten_column(keyword),
        description = trimws(sf_remove(description, "\n")),
        references = delist(references)
      )
  ) |&gt;
    roworder(title)

main &lt;- add_vars(
  main,
  contact = glue('{delist(get_elem(main$contactPoint, "fn"))} ',
                 '({delist(get_elem(main$contactPoint, "^has", regex = TRUE))})') |&gt; 
        as.character()
      ) |&gt; 
  slt(-contactPoint)

main |&gt; 
  rsplit(~ periodicity)

