                    }
                }
            }
        },
        "license": {
            "ui:options": {
                "widget": "list",
                "type": "select_other",
                "other_type": "url",
                "source": {
                    "enum": [
                        "https://creativecommons.org/licenses/by/4.0/",
                        "https://creativecommons.org/licenses/by-sa/4.0/",
                        "https://creativecommons.org/publicdomain/zero/1.0/",
