    "publisher": {
        "title": "Publisher",
        "description": "A Dataset Publisher Organization.",
        "type": "object",
        "required": [
            "identifier",
            "data"
        ],
        "properties": {
            "identifier": {
                "title": "Identifier",
                "type": "string"
            },
            "data": {
                "$schema": "http://json-schema.org/draft-04/schema#",
                "id": "https://project-open-data.cio.gov/v1.1/schema/organization.json#",
                "title": "Organization",
                "description": "A Dataset Publisher Organization.",
                "type": "object",
                "required": [
                    "name"
                ],
                "properties": {
                    "@type": {
                        "title": "Metadata Context",
                        "description": "IRI for the JSON-LD data type. This should be org:Organization for each publisher",
                        "type": "string",
                        "default": "org:Organization"
                    },
                    "name": {
                        "title": "Publisher Name",
                        "description": "",
                        "type": "string",
                        "minLength": 1
                    },
                    "subOrganizationOf": {
                        "title": "Parent Organization",
                        "type": "string"
                    }
                }
            }
        }
    },
