    "distribution": {
        "title": "Distribution",
        "description": "Distribution object, based on DCAT-US.",
        "type": "object",
        "required": [
            "identifier",
            "data"
        ],
        "properties": {
            "identifier": {
                "title": "Identifier",
                "type": "string"
            },
            "data": {
                "title": "Data File",
                "type": "object",
                "properties": {
                    "@type": {
                        "title": "Metadata Context",
                        "description": "IRI for the JSON-LD data type. This should be dcat:Distribution for each Distribution.",
                        "default": "dcat:Distribution",
                        "type": "string",
                        "readOnly": true
                    },
                    "title": {
                        "title": "Title",
                        "description": "Human-readable name of the file.",
                        "type": "string",
                        "minLength": 1
                    },
                    "description": {
                        "title": "Description",
                        "description": "Human-readable description of the file.",
                        "type": "string",
                        "minLength": 1
                    },
