4.1 Query Generation
format_syntax &lt;- \(name, value) {
  setNames(value, paste0(name, "[id-", seq_along(value), "][condition][value]"))
}

format_syntax(name = "filter", value = c("GA", "NY"))

filter[id-1][condition][value] filter[id-2][condition][value] 
                          "GA"                           "NY" 

request("https://example.com") |&gt;
  req_url_query(!!!format_syntax("filter", c("GA", "NY")))

&lt;httr2_request&gt;


GET
https://example.com/?filter%5Bid-1%5D%5Bcondition%5D%5Bvalue%5D=GA&filter%5Bid-2%5D%5Bcondition%5D%5Bvalue%5D=NY


Body: empty

7.1 S7 Sketches
PATH &lt;- new_class(
  name = "PATH",
  properties = list(
    head = new_property(
      class_character,
      default = NA_character_,
      setter = function(self, value) {
        self@head &lt;- value
        self
      }),
    uuid = new_property(
      class_character,
      default = NA_character_,
      setter = function(self, value) {
        self@uuid &lt;- value
        self
      }),
    tail = new_property(
      class_character,
      default = NA_character_,
      setter = function(self, value) {
        self@tail &lt;- value
        self
      })
  ),
  validator = function(self) { 
    if (length(self@head) != 1L) {
      "@head must be length 1"
    } else if (length(self@uuid) != 1L) {
      "@uuid must be length 1"  
    } else if (length(self@tail) != 1L) {
      "@tail must be length 1" 
    }
  }
)

URL &lt;- new_class(
  name = "URL",
  properties = list(
    scheme = new_property(class_character, default = "https"),
    host = new_property(class_character, default = "data.cms.gov"),
    path = new_property(class = PATH)
  )
)

