accessURL &lt;- distribution |&gt; 
  filter(
    grepl("Opt Out Affidavits : ", title), 
    format == "API",
    description == "latest") |&gt; 
  pull(accessURL) |&gt; 
  request() |&gt; 
  req_url_query(size = 5000, !!!query)

